import axios  from "axios";
import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom"

export default function UpdateTask(){
    let nav = useNavigate();
    let {taskId} = useParams();
    const [task, setTask] = useState();
    const [title,setTitle] = useState('')
    const [description,setDescription] = useState('')
    const [status,setStatus] = useState('')
    const statusOptions = ["In Progress","Completed"]

    useEffect(() => { async function fetchTask(){
        if(taskId){
            const baseURL = 'http://localhost:8082/tasks';
            await axios.get(`${baseURL}/${taskId}`)
            .then((resp) => {setTask(resp.data);setTitle(resp.data.title);setDescription(resp.data.description);setStatus(resp.data.status)})}
     }
     fetchTask()
    })

    function update(){
        if(title && description && status)
            console.log()
    }

    return(
        <div>
            <h1>Update a task</h1>
            {task && 
            <div>
                <label>
                Title : 
                <input type="text" value={title} onChange={event => setTitle(event.target.value)}/>
            </label>
            <br/>
            <label>
                Description : 
                <input type="text" value={task.title} onChange={event => setDescription(event.target.value)} />
            </label>
            <br/>
            <label>
                Status : 
                <select type="text" value={task.status} onChange={event => setStatus(event.target.value)}>
                    {statusOptions.map(o => (
                        <option value={o} key={o}>{o}</option>
                    ))}
                </select>
            </label>
            <br/>
            <button type="update" onClick={update}>Update</button>
            </div>}
            {!task && <h3>There is no task with this id</h3>}
            <h5 onClick={() => {nav("/");}}>Go to home</h5>
        </div>
    )
}

const UpdateTaskView = (props) => {
    return (
      <div>
            <ul>
                <li>Id: {props.selectedTask._id}</li>
                <li>Title: {props.selectedTask.title}</li>
                <li>Description: {props.selectedTask.description}</li>
                <li>Status: {props.selectedTask.status}</li>
            </ul>
      </div>
    )
  }
