import { useState, useEffect } from "react";
import axios  from "axios";
import { useNavigate } from "react-router-dom"


export default function ReadTasks(){
    let nav = useNavigate();
    const [tasks, setTasks] = useState([]);
    const [selectedTask, setSelectedTask] = useState(null);

    useEffect(  () =>{

        axios.get('http://localhost:8082/tasks')
        .then((resp) => setTasks(resp.data))
    
     }, [])

     function TaskView(){
        return (
          <div>
                <ul>
                    <li>Id: {selectedTask._id}</li>
                    <li>Title: {selectedTask.title}</li>
                    <li>Description: {selectedTask.description}</li>
                    <li>State: {selectedTask.status}</li>
                </ul>
          </div>
        )}
    return(
        <div>
            <h1>Read tasks</h1>
            <ul>
                {tasks.map(t => (
                    <li key={t._id} onClick={() => {setSelectedTask(t)}} >{t.status}</li>
                ))}
            </ul>
            <div>
                <h2>Task details</h2>
                    {(selectedTask) ? <TaskView selectedUser={selectedTask}/> : <EmptyView/>}
            </div>
            <h5 onClick={() => {nav("/");}}>Go to home</h5>
        </div>
    )
}
function EmptyView() {
    return (
      <div>
        <p>Select task to display details</p>
      </div>
    )
  }
